import React, {useState} from 'react'
 
const ItemCount =({stock,initial,onAdd})=> {
    const [count,setCount]= useState(initial);
    
    const sumaCount = ()=> {
        if(count<stock){
            setCount(count+1);
        }
       
    };
    
       const restaCount = ()=>{
        if(count>0){
            setCount(count-1);
          
        }
        
    };

     
  return (
  <>
  
 
 <button className='botones'
 variant ="outline-primary"
 onClick={restaCount}>
     {" - "}
 </button>
 <button className='botones'
 variant ="outline-primary"
 onClick={sumaCount}>
     {" + "}
 </button>  
  
   </>
  );
}

export default ItemCount
