import {useState}   from "react";
 

function ItemCount  ({ stock,initial, onAdd })   {
  const [counter, setCounter] = useState(initial);

  function handleIncrement() {
      if(counter <stock){
          setCounter(counter+1)
      }
  }

  function handleDecrement(){ 
    if(counter>initial){
        setCounter(counter-1)
    }
}

const nAdd = ()=> {
   alert(counter)
}
   

  return (
      <div className="container">
          <div className="row">
              <div className="col-1">
                  
              <div className="row">
                  <div className="d-flex justify-content-around col-1">
                      <button className="btn-sm btn-success" onClick={handleDecrement}> - </button>
                      <span className="mx-5"> {counter} </span>
                      <button className="btn-sm btn-success" onClick={handleIncrement}> + </button>
                  </div>

                  <div className="mt-5 col-1">
                  <button className="btn btn-outline-success" onClick={nAdd}>Agregar al carrito</button>
                      
                  </div>
                  
                  </div>
                   </div>

      </div>
      </div>
  );
}

export default ItemCount;
