import React, {useState} from 'react'
import CartWigdet from '../components/Widget/CartWigdet';
 
const ItemCount =({stock,initial,onAdd})=> {
    const [count,setCount]= useState(initial);
    
    const sumaCount = ()=> {
        if(count<stock){
            setCount(count+1);
        }
       
    };
    
       const restaCount = ()=>{
        if(count>0){
            setCount(count-1);
          
        }
        
    };

    const mostrarMensaje =()=> ("pelotudo");
     
  return (
  <>
  
 
 <button className='botones'
 variant ="outline-primary"
 onClick={restaCount}>
     {" - "}
 </button>
 <button className='botones'
 variant ="outline-primary"
 onClick={sumaCount}>
     {" + "}
 </button>  
  <div>
      <button className='boton agregar' variant="outline-success" onClick={()=>mostrarMensaje()}
      >
          
      </button>
  </div>
   </>
  );
}

export default ItemCount;
