import React, {useState} from 'react'
 
const ItemCount =({stock,initial,onAdd})=> {
    const [count,setCount]= useState(initial);
    
    if (count <stock)
    setCount()

    
    
    useEffect(() =>{ 
        return()=>{};
    });

    const restaCount = ()=>{
        if(!stock==0){
            setCount(count-1);
            onAdd=console.log("no hay producto en stock");
        }
        
    };

    const sumaCount = ()=>{
        setCount(count+1);
    };

    const mostrarAlerta = ()=> ("Agregadp añ carrotp");

  return (
  <>
<h4>
    {count}
</h4>
<button className='botones'
variant ="outline-primary"
onClick={restaCount}>
    {"-"}
</button>
<button className='botones'
variant ="outline-primary"
onClick={()=> sumaCount()}>
    {"+"}
</button>
   </>
  )
}

export default ItemCount
