import {useEffect} from 'react' 
import React from 'react'



function ItemCount() {
    const [count,setCount]= useState(1);
    
    useEffect(() =>{ 
        return()=>{};
    });

    const restaCount = ()=>{
        setCount(count-1);
    };

    const sumaCount = ()=>{
        setCount(count+1);
    };

    const mostrarAlerta = ()=> ("Agregadp añ carrotp");

  return (
  <>
<h4>
    Nuevo producto
</h4>
<button className='botones'
variant ="outline-primary"
onClick={restaCount}>
    {"-"}
</button>
<button className='botones'
variant ="outline-primary"
onClick={sumaCount}>
    {"+"}
</button>

  </>
  )
}

export default ItemCount
